import React, { useState, useEffect } from "react";
import {addDoc, collection} from 'firebase/firestore';
import { db, auth } from '../firebase-config';
import {useNavigate} from 'react-router-dom';

import { Editor } from "react-draft-wysiwyg";
import { ContentState, convertToRaw } from "draft-js";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

function CreatePost({ isAuth }) {

    const [title, setTitle] = useState("");
    const [postText, setPostText] = useState("");

    const postsCollectionRef = collection(db, "posts");

    let navigate = useNavigate();

    const createPost = async () => {
        await addDoc(postsCollectionRef, {
            title, 
            postText, 
            author: { name: auth.currentUser.displayName, id: auth.currentUser.uid },
        });
        navigate("/");
    };

    useEffect(() => {
        if(!isAuth) {
            navigate("login");
        }
    }, []);

    let _editorState = ContentState.createFromText('Sample content state');
    const raw = convertToRaw(_editorState)

    const [editorState, setEditorState] = useState(raw);

    return (
        <div className="createPostPage"> 
            <div className="cpContainer">
                <h1> Create a Post</h1>
                <div className="inputGp">
                    <label> Title: </label>
                    <input placeholder="Title..." 
                        onChange={(event) => {
                            setTitle(event.target.value)
                            }
                        }
                    />
                </div>
                <br></br>
                <Editor 
                    editorState={editorState}
                    onContentStateChange={setEditorState}
                    toolbarClassName="toolbarClassName"
                    wrapperClassName="wrapperClassName"
                    editorClassName="editorClassName" 
                />
                <button onClick={() => {createPost(); console.log(editorState)}}> Submit Post</button>
            </div>
        </div>
    );
}

export default CreatePost;
