import React, { useState, useEffect } from "react";
import {collection, getDocs, deleteDoc, doc, query, where} from 'firebase/firestore';
import { auth, db } from '../firebase-config';
import { Link } from 'react-router-dom';

function Home({ isAuth }) {
    const [postLists, setPostList] = useState([]);

    const [chapter5, setChapter5] = useState([]);

    const [chapter6, setChapter6] = useState([]);

    const [openSection, setOpenSection] = useState({
        chapter5: 'false',
        chapter6: 'false',
        chapter7: 'false',
        chapter8: 'false',
        chapter9: 'false',
        chapter10: 'false',
        chapter11: 'false',
        chapter12: 'false',
    });

    const postsCollectionRef = collection(db, "posts");

    const deletePost = async (id) => {
        const postDoc = doc(db, "posts", id);
        await deleteDoc(postDoc);
    }

    useEffect(() => {
        const getPosts = async () => {
            const data = await getDocs(postsCollectionRef);
            setPostList(data.docs.map((doc) => (
                { ...doc.data(), id: doc.id}
                )
            ));
        };
        getPosts();
    }, [deletePost]);

    const q5 = query(postsCollectionRef, where("Chapter", '==', "5"));
    const q6 = query(postsCollectionRef, where("Chapter", '==', "6"));
    const q7 = query(postsCollectionRef, where("Chapter", '==', "7"));
    const q8 = query(postsCollectionRef, where("Chapter", '==', "8"));
    const q9 = query(postsCollectionRef, where("Chapter", '==', "9"));
    const q10 = query(postsCollectionRef, where("Chapter", '==', "10"));
    const q11 = query(postsCollectionRef, where("Chapter", '==', "11"));
    const q12 = query(postsCollectionRef, where("Chapter", '==', "12"));

    const chapterFiveQuery = async () => {
        const docData = await getDocs(q5);
        setChapter5(docData.docs.map((doc) => (
                {...doc.data(), id: doc.id}
            )
        ));
    }

    const chapterSixQuery = async () => {
        const docData = await getDocs(q6);
        setChapter6(docData.docs.map((doc) => (
                {...doc.data(), id: doc.id}
            )
        ));
    } 

    return (
        <div>
            <div className="heroSection">

            </div>
            <div className="homePage">
                <div className="HomeSections">
                    <div className="HomeSection1">
                        <button onClick={() => {chapterFiveQuery(); setOpenSection(prevState => {return {...prevState, chapter5: true}}) }}>Open section</button>
                        <button onClick={() => setOpenSection(prevState => {return {...prevState, chapter5: false}}) }>Close Section</button>
                        {openSection.chapter5 === true && chapter5.map((post) => {
                            return <div className="post"> 
                                        <div className="postHeader">
                                            <div className="title">
                                                <h1> {post.title} </h1>
                                            </div>
                                            <div className="deletePost">
                                                {isAuth && post.author.id === auth.currentUser.uid && (
                                                    <button 
                                                        onClick={() => {
                                                            deletePost(post.id);
                                                        }}> X 
                                                    </button>
                                                )}
                                            </div>
                                        </div>
                                        <button key={post.id}>
                                            <Link to={{pathname: '/thepost/'+post.id}}> View Post</Link>
                                        </button>
                                    <div className="postTextContainer"> {post.postText} </div>
                                    <h3>@{post.author.name}</h3>
                            </div>              
                        })}
                    </div>
                    <div className="HomeSection2">       
                        <button onClick={() => {chapterSixQuery(); setOpenSection(prevState => {return {...prevState, chapter6: true}}) }}>Open section</button>
                        <button onClick={() => setOpenSection(prevState => {return {...prevState, chapter6: false}}) }>Close Section</button>
                        {openSection.chapter6 === true && chapter6.map((post) => {
                            return <div className="post"> 
                                        <div className="postHeader">
                                            <div className="title">
                                                <h1> {post.title} </h1>
                                            </div>
                                            <div className="deletePost">
                                                {isAuth && post.author.id === auth.currentUser.uid && (
                                                    <button 
                                                        onClick={() => {
                                                            deletePost(post.id);
                                                        }}> X 
                                                    </button>
                                                )}
                                            </div>
                                        </div>
                                        <button key={post.id}>
                                            <Link to={{pathname: '/thepost/'+post.id}}> View Post</Link>
                                        </button>
                                    <div className="postTextContainer"> {post.postText} </div>
                                    <h3>@{post.author.name}</h3>
                            </div>
                        })}
                    </div>
                </div>
                {postLists.map((post) => {
                    return <div className="post"> 
                                <div className="postHeader">
                                    <div className="title">
                                        <h1> {post.title} </h1>
                                    </div>
                                    <div className="deletePost">
                                        {isAuth && post.author.id === auth.currentUser.uid && (
                                            <button 
                                                onClick={() => {
                                                    deletePost(post.id);
                                                }}> X 
                                            </button>
                                        )}
                                    </div>
                                </div>
                                <button key={post.id}>
                                    <Link to={{pathname: '/thepost/'+post.id}}> View Post</Link>
                                </button>
                            <div className="postTextContainer"> {post.postText} </div>
                            <h3>@{post.author.name}</h3>
                    </div>
                })}
            </div>
        </div>
    );
}

export default Home;
