import React, { useState, useEffect } from "react";
import {addDoc, collection} from 'firebase/firestore';
import { db, auth } from '../firebase-config';
import {useNavigate} from 'react-router-dom';

import { Editor } from "react-draft-wysiwyg";
import { draftToHtml } from 'draftjs-to-html'; 
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";
import { EditorState } from "draft-js";
import { convertToRaw } from "draft-js";

function CreatePost({ isAuth }) {

    const [title, setTitle] = useState("");
    const [postText, setPostText] = useState("");

    const [editorState, setEditorState] = useState(
        () => EditorState.createEmpty(),
        {
            message: "Enter your post here!",
            rawMessage: '',
        },
    );

    const postsCollectionRef = collection(db, "posts");

    let navigate = useNavigate();

    const createPost = async () => {
        await addDoc(postsCollectionRef, {
            title, 
            postText, 
            author: { name: auth.currentUser.displayName, id: auth.currentUser.uid },
        });
        navigate("/");
    };

    useEffect(() => {
        if(!isAuth) {
            navigate("login");
        }
    }, []);

    const onEditorStateChange = (editorState) => {
        this.editorState({
            editorState,
            rawMessage: draftToHtml(convertToRaw(editorState.getCurrentContent()))
        })
    }

    const handleEditorStateMessage = () => {
        this.editorState({
            message : editorState.rawMessage
        })
    }

    return (
        <div className="createPostPage"> 
            <div className="cpContainer">
                <h1> Create a Post</h1>
                <div className="inputGp">
                    <label> Title: </label>
                    <input placeholder="Title..." 
                        onChange={(event) => {
                            setTitle(event.target.value)
                            }
                        }
                    />
                </div>
                <br></br>
                <Editor 
                    initialEditorState={editorState}
                    onEditorStateChange={onEditorStateChange}
                    toolbarClassName="toolbarClassName"
                    wrapperClassName="wrapperClassName"
                    editorClassName="editorClassName" 
                />
                <button onClick={() => {createPost(); handleEditorStateMessage}}> Submit Post</button>
            </div>
        </div>
    );
}

export default CreatePost;
