import { useParams } from "react-router-dom";
import React, { useEffect, useState } from "react";
import { auth, db } from '../firebase-config';
import { collection, doc, getDoc } from 'firebase/firestore';

function ThePost({ isAuth }) {

    const { id } = useParams();

    const postsCollectionRef = collection(db, "posts");

    const [docDetails, setDocDetails] = useState([]);

    const [seeAuthor, setAuthor] = useState([
        {
            authorName: "",
            authorID: "",
        }
    ]);

    useEffect(() => {
        const getPost = async (postID) => {
            const postDoc = doc(postsCollectionRef, postID);
            const docSnap = await getDoc(postDoc);
            setDocDetails(docSnap.data());

            if(docSnap.exists()) {
                setAuthor(prevState => {return {...prevState, authorName: docSnap.data().author.name}});
                console.log(docDetails.author.id);
            } else {
                console.log("Error");
            }
        };
        getPost(id);
    }, []);

    return (
        <div className="thePost"> 
            <div className="postHeader">
                <h1> {docDetails.title} </h1>
            </div>
            <hr />
            <div className="postTextContainer"> {docDetails.postText} </div>
                <h3> 
                    @{seeAuthor}
                </h3>
            </div>
    );
}

export default ThePost;
