import React, { useState, useEffect } from "react";
import {collection, getDocs, deleteDoc, doc, query, where} from 'firebase/firestore';
import { auth, db } from '../firebase-config';
import { Link } from 'react-router-dom';

function Home({ isAuth }) {
    const [chapter, setChapter] = useState([]);

    const [openSection, setOpenSection] = useState({
        chapter5: 'false',
        chapter6: 'false',
        chapter7: 'false',
        chapter8: 'false',
        chapter9: 'false',
        chapter10: 'false',
        chapter11: 'false',
        chapter12: 'false',
    });

    const postsCollectionRef = collection(db, "posts");

    const deletePost = async (id) => {
        const postDoc = doc(db, "posts", id);
        await deleteDoc(postDoc);
    }

    const chapterQuery = async (qOption) => {
        const q = query(postsCollectionRef, where("Chapter", '==', qOption));
        const docData = await getDocs(q);
        setChapter(docData.docs.map((doc) => (
                {...doc.data(), id: doc.id}
            )
        ));
    }

    return (
        <div>
            <div className="heroSection">

            </div>
                <div className="HomeSections">
                    <div className="HomeSection1">
                        <img className="topicImg" src={require("../assets/Importance-of-project-scope-management.jpeg")} alt="Scope management photo"/>
                        <h1 className="sectionTitle">Topics in Project Scope Management?</h1>
                        <span className="openBtn" onClick={() => 
                                            {chapterQuery("5"); 
                                                setOpenSection(prevState => {return {...prevState, 
                                                    chapter5: true, 
                                                    chapter6: false, 
                                                    chapter7: false, 
                                                    chapter8: false, 
                                                    chapter9: false, 
                                                    chapter10: false, 
                                                    chapter11: false, 
                                                    chatper12: false}}) 
                                            }}
                        >Open Section</span>
                        <span className="openBtn" onClick={() => setOpenSection(prevState => {return {...prevState, chapter5: false}}) }>Close Section</span>
                        {openSection.chapter5 === true && chapter.map((post) => {
                            return <div className="post"> 
                                        <hr />
                                        <div className="postHeader">
                                            <div className="title">
                                                <h1> {post.title} </h1>
                                            </div>
                                            <div className="deletePost">
                                                {isAuth && post.author.id === auth.currentUser.uid && (
                                                    <button 
                                                        onClick={() => {
                                                            deletePost(post.id);
                                                        }}> X 
                                                    </button>
                                                )}
                                            </div>
                                        </div>
                                        <div> 
                                            <button key={post.id}>
                                                <Link to={{pathname: '/thepost/'+post.id}}> View Post</Link>
                                            </button>
                                        </div>
                                    <div className="postTextContainer"> {post.postText} </div>
                                    <h3>@{post.author.name}</h3>
                            </div>              
                        })}
                    </div>
                </div>
        </div>
    );
}

export default Home;
