import { useLocation } from "react-router-dom";
import React, { useEffect, useState, Component } from "react";
import { db } from '../firebase-config';
import { doc, DocumentSnapshot, getDoc, getDocs } from 'firebase/firestore';

function ThePost() {

    const location = useLocation();
    const { postInfo } = location.state;

    const [docDetails, setDocDetails] = useState([]);

    const getPost = async (id) => {
        const postDoc = doc(db, "posts", id);
        const docSnap = await getDoc(postDoc);
        setDocDetails(docSnap.data());

        if(docSnap.exists()) {
            console.log("Document data: ", docDetails);
        } else {
            console.log("Error");
        }
    };

    getPost(postInfo);

    return (
        <div className="homePage">
            <div className="post"> 
                <div className="post"> 
                    <div className="postHeader">
                        <h1> {docDetails.title} </h1>
                    </div>
                    <div className="postTextContainer"> {docDetails.postText} </div>
                </div>
            </div>
        </div>  
    );
}

export default ThePost;
