import React, { useState, useEffect } from "react";
import {collection, getDocs, deleteDoc, doc, query, where} from 'firebase/firestore';
import { auth, db } from '../firebase-config';
import { Link } from 'react-router-dom';

function Home({ isAuth }) {
    const [postLists, setPostList] = useState([]);

    const [chapter, setChapter] = useState([]);

    const [openSection, setOpenSection] = useState({
        chapter5: 'false',
        chapter6: 'false',
        chapter7: 'false',
        chapter8: 'false',
        chapter9: 'false',
        chapter10: 'false',
        chapter11: 'false',
        chapter12: 'false',
    });

    const postsCollectionRef = collection(db, "posts");

    const deletePost = async (id) => {
        const postDoc = doc(db, "posts", id);
        await deleteDoc(postDoc);
    }

    useEffect(() => {
        const getPosts = async () => {
            const data = await getDocs(postsCollectionRef);
            setPostList(data.docs.map((doc) => (
                { ...doc.data(), id: doc.id}
                )
            ));
        };
        getPosts();
    }, [deletePost]);

    const chapterQuery = async (qOption) => {
        const q = query(postsCollectionRef, where("Chapter", '==', qOption));
        const docData = await getDocs(q);
        setChapter(docData.docs.map((doc) => (
                {...doc.data(), id: doc.id}
            )
        ));
    }

    return (
        <div>
            <div className="heroSection">

            </div>
                <div className="HomeSections">
                    <div className="HomeSection1">
                        <img src={require("../assets/Importance-of-project-scope-management.jpeg")} alt="Scope management photo" width={"300px"} height={"300px"}/>
                        <h1>Want to Know About Project Scope Management?</h1>
                        <button onClick={() => 
                                            {chapterQuery("5"); 
                                                setOpenSection(prevState => {return {...prevState, 
                                                    chapter5: true, 
                                                    chapter6: false, 
                                                    chapter7: false, 
                                                    chapter8: false, 
                                                    chapter9: false, 
                                                    chapter10: false, 
                                                    chapter11: false, 
                                                    chatper12: false}}) 
                                            }}
                        >
                            Open section
                        </button>
                        <button onClick={() => setOpenSection(prevState => {return {...prevState, chapter5: false}}) }>Close Section</button>
                        {openSection.chapter5 === true && chapter.map((post) => {
                            return <div className="post"> 
                                        <div className="postHeader">
                                            <div className="title">
                                                <h1> {post.title} </h1>
                                            </div>
                                            <div className="deletePost">
                                                {isAuth && post.author.id === auth.currentUser.uid && (
                                                    <button 
                                                        onClick={() => {
                                                            deletePost(post.id);
                                                        }}> X 
                                                    </button>
                                                )}
                                            </div>
                                        </div>
                                        <button key={post.id}>
                                            <Link to={{pathname: '/thepost/'+post.id}}> View Post</Link>
                                        </button>
                                    <div className="postTextContainer"> {post.postText} </div>
                                    <h3>@{post.author.name}</h3>
                            </div>              
                        })}
                    </div>
                    <div className="HomeSection1"> 
                        <h1>Want to Know About Project Schedule Management?</h1>      
                    <button onClick={() => 
                                            {chapterQuery("6"); 
                                                setOpenSection(prevState => {return {...prevState, 
                                                    chapter5: false, 
                                                    chapter6: true, 
                                                    chapter7: false, 
                                                    chapter8: false, 
                                                    chapter9: false, 
                                                    chapter10: false, 
                                                    chapter11: false, 
                                                    chatper12: false}}) 
                                            }}
                        >
                            Open section
                        </button>
                        <button onClick={() => setOpenSection(prevState => {return {...prevState, chapter6: false}}) }>Close Section</button>
                        {openSection.chapter6 === true && chapter.map((post) => {
                            return <div className="post"> 
                                        <div className="postHeader">
                                            <div className="title">
                                                <h1> {post.title} </h1>
                                            </div>
                                            <div className="deletePost">
                                                {isAuth && post.author.id === auth.currentUser.uid && (
                                                    <button 
                                                        onClick={() => {
                                                            deletePost(post.id);
                                                        }}> X 
                                                    </button>
                                                )}
                                            </div>
                                        </div>
                                        <button key={post.id}>
                                            <Link to={{pathname: '/thepost/'+post.id}}> View Post</Link>
                                        </button>
                                    <div className="postTextContainer"> {post.postText} </div>
                                    <h3>@{post.author.name}</h3>
                            </div>
                        })}
                    </div>
                </div>
            <div className="homePage">
                {postLists.map((post) => {
                    return <div className="post"> 
                                <div className="postHeader">
                                    <div className="title">
                                        <h1> {post.title} </h1>
                                    </div>
                                    <div className="deletePost">
                                        {isAuth && post.author.id === auth.currentUser.uid && (
                                            <button 
                                                onClick={() => {
                                                    deletePost(post.id);
                                                }}> X 
                                            </button>
                                        )}
                                    </div>
                                </div>
                                <button key={post.id}>
                                    <Link to={{pathname: '/thepost/'+post.id}}> View Post</Link>
                                </button>
                            <div className="postTextContainer"> {post.postText} </div>
                            <h3>@{post.author.name}</h3>
                    </div>
                })}
            </div>
        </div>
    );
}

export default Home;
